          trigger: 'blur'
        }],
        quantity: [{
          required: true,
          message: ' ',
          trigger: 'blur'
        }]
      }
    };
  },
  computed: _objectSpread({}, mapState({
    dt_stock_type: function dt_stock_type(state) {
      return state.dict.dt_stock_type;
    },
    dt_stock_status: function dt_stock_status(state) {
      return state.dict.dt_stock_status;
    }
  })),
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    }; //   this.$store.dispatch('getDicData',['dt_stock_type','dt_stock_status']);

  },
  methods: {
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 30;
    },
    changeMoudle: function changeMoudle(val) {
      this.$emit('changeSelect', val);
    },
    getList: function getList() {},
    // 表格选择框选中 注:参数为选中的所有行的数组
    selectChange: function selectChange(val) {
      if (this.isSingle) {
        if (val.length > 1) {
          this.$refs.tb.clearSelection(); //清除其他行的选中

          this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected"); //单击行绑定点击事件
        } else if (val.length === 1) {
          this.selectedRows = val;
          this.currentSelectedRow = val[val.length - 1];
        }
      } else {
        this.selectedRows = val;
        this.currentSelectedRow = val[val.length - 1];
      }
    },
    //点击表格某一行
    rowClick: function rowClick(val) {
      if (this.isSingle) {
        this.$refs.tb.clearSelection(); //清除其他行的选中
      }

      this.$refs.tb.toggleRowSelection(val, "selected"); //单击行绑定点击事件

      this.getSubList(val);
    },
    timeFormat: function timeFormat() {
      var d = new Date();
      var year = d.getFullYear(); //年  

      var month = d.getMonth() + 1; //月  

      var day = d.getDate(); //日  

      var clock = year + "-";
      if (month < 10) clock += "0";
      clock += month + "-";
      if (day < 10) clock += "0";
      clock += day + " ";
      return clock;
    },
    //新增
    handleAdd: function handleAdd() {},
    //删除
    handleDelete: function handleDelete() {},
    //保存
    handleSave: function handleSave() {},
    //刷新
    handleRefresh: function handleRefresh() {
      this.isEdit = false;
      this.isSaveEdit = false;
      this.list = [];
    }
  }
};
