import * as api from "@/api/project/srm/fileUpload/upload";
export default {
  props: ['functionCode', 'fileType'],
  data: function data() {
    return {
      dialogVisible: false,
      list: [],
      files: [],
      fileList: [],
      filename: ''
    };
  },
  methods: {
    getList: function getList() {
      var _this = this;

      var dto = {
        currentPage: 1,
        pageSize: 10,
        functionCode: this.functionCode,
        fileType: this.fileType
      };
      api.queryRecords(dto).then(function (res) {
        console.log(res, 'hhhhh');
        _this.list = res.data.list;
      });
    },
    handleClose: function handleClose() {},
    handleDelete: function handleDelete(index, row) {
      var _this2 = this;

      console.log(index, row);
      var dto = {
        fileCode: row.fileCode
      };
      api.deleteRecord(dto).then(function () {
        _this2.$message({
          title: '成功',
          message: '删除成功',
          type: 'success',
          duration: 2000
        });

        _this2.getList();
      });
    },
    fileLimit: function fileLimit(file) {
      var isLt10M = file.size / 1024 / 1024 < 10;

      if (!isLt10M) {
        this.$message({
          message: '上传文件大小不能超过 10MB!',
          type: 'warning'
        });
      }

      return isLt10M;
    },
    uploadFile: function uploadFile() {},
    fileExceed: function fileExceed() {
      this.$message.error('一次最多上传10个文件');
    },
    handleFile: function handleFile(file, fileList) {
      this.files = fileList;
      console.log(this.files, ' this.files');
    },
    handleUpload: function handleUpload() {
      var _this3 = this;

      console.log(this.functionCode, this.fileType, 'fileType');

      if (!this.files.length) {
        this.$message({
          title: '警告',
          message: '请选择文件',
          type: 'warning'
        });
        return false;
      }

      var data = new FormData();
      this.files.forEach(function (file) {
        data.append('files', file.raw);
      });
      var dto = {
        // functionCode: this.functionCode,
        fileType: this.fileType,
        category: 'srm'
      };
      console.log(data, 'datadata');
      api.upload(this.functionCode, data, dto).then(function (res) {
        console.log(res, 'kkkkk');

        if (res.status == 200) {
          _this3.$message({
            title: '成功',
            message: '上传成功',
            type: 'success',
            duration: 2000
          });
        }

        _this3.getList();

        _this3.files = [];
        _this3.fileList = [];
      });
    },
    // 表格选择框选中 注:参数为选中的所有行的数组
    selectChange: function selectChange(val) {
      this.selectlistRow = val[val.length - 1];

      if (val.length > 1) {
        this.$refs.tb.clearSelection();
        this.$refs.tb.toggleRowSelection(val.pop());
      }

      this.$refs.tb.setCurrentRow(this.selectlistRow);
    },
    handleDownload: function handleDownload(index, row) {
      window.open("http://localhost:7954/basic/files/download?readName=".concat(row.readName));
    }
  }
};
