import request from '@/utils/request'; // 查询备件参数

export function queryRecords(query) {
  return request({
    url: "/spm/base/parameter",
    method: 'get',
    params: query
  });
} // 查询单个备件参数

export function queryRecord(orgCode, materielCode) {
  return request({
    url: "/spm/base/parameter/".concat(orgCode, "/materiel/").concat(materielCode),
    method: 'get'
  });
} // 删除备件参数

export function deleteRecords(orgCode, materielCode) {
  return request({
    url: "/spm/base/parameter/".concat(orgCode, "/materiel/").concat(materielCode),
    method: 'delete'
  });
} // 新增备件参数

export function addRecords(data) {
  return request({
    url: '/spm/base/parameter',
    method: 'post',
    data: data
  });
} // 修改备件请购

export function patchRecords(orgCode, materielCode, data) {
  return request({
    url: "/spm/base/parameter/".concat(orgCode, "/materiel/").concat(materielCode),
    method: 'patch',
    data: data
  });
}
