import request from '@/utils/request'; // 查询

export function queryRecords(query) {
  return request({
    url: '/ems/dicts',
    method: 'get',
    params: query
  });
} // 查询单个

export function getRecord(dcitCode) {
  return request({
    url: "/ems/dicts/".concat(dcitCode),
    method: 'get'
  });
} // 新增

export function addRecord(data) {
  return request({
    url: '/ems/dicts',
    method: 'post',
    data: data
  });
} // 更新

export function updateRecord(dictCode, data) {
  return request({
    url: "/ems/dicts/".concat(dictCode),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteRecord(supplierCode) {
  return request({
    url: "/ems/dicts/".concat(supplierCode),
    method: 'delete'
  });
}
