import QRCode from 'qrcode'; //二维码

var canvas = '';
export default {
  props: ['imgindex'],
  methods: {
    qrCreate: function qrCreate(orderNum) {
      var _this = this;

      QRCode.toCanvas(canvas, orderNum, function (error) {
        if (!error) {
          document.getElementById("imgQr" + _this.imgindex).src = canvas.toDataURL();
        }
      });
    }
  },
  mounted: function mounted() {
    canvas = document.getElementById('qrccode-canvas' + this.imgindex);
  }
};
