import request from '@/utils/request';
export function queryRecords(query) {
  return request({
    url: '/base-modeling/measure-units',
    method: 'get',
    params: query
  });
}
export function getRecord(colorCode) {
  return request({
    url: '/base-modeling/measure-units' + '/' + colorCode,
    method: 'get'
  });
}
export function addRecord(data) {
  return request({
    url: '/base-modeling/measure-units',
    method: 'post',
    data: data
  });
}
export function updateRecord(colorCode, data) {
  return request({
    url: '/base-modeling/measure-units' + '/' + colorCode,
    method: 'patch',
    data: data
  });
}
export function deleteRecord(colorCode) {
  return request({
    url: '/base-modeling/measure-units' + '/' + colorCode,
    method: 'delete'
  });
}
