import request from '@/utils/request'; //查询点巡检计划

export function queryRecords(query) {
  return request({
    url: '/easDevice/ledger/inspection/plan',
    method: 'get',
    params: query
  });
} //创建点巡检计划

export function addRecords(data) {
  return request({
    url: '/easDevice/ledger/inspection/plan',
    method: 'post',
    data: data
  });
} //修改点巡检计划

export function patchRecords(inspectionPlanCode, data) {
  return request({
    url: "/easDevice/ledger/inspection/plan/".concat(inspectionPlanCode),
    method: 'patch',
    data: data
  });
} //删除点巡检计划

export function deleteRecords(inspectionPlanCode) {
  return request({
    url: "/easDevice/ledger/inspection/plan/".concat(inspectionPlanCode),
    method: 'delete'
  });
} //点巡检计划停用

export function changeRecords(inspectionPlanCode, num) {
  return request({
    url: "/easDevice/ledger/inspection/plan/".concat(inspectionPlanCode, "/enable?isEnableEnum=").concat(num),
    method: 'patch'
  });
} //查询点巡检计划设备

