import { selectlists as getOrgList } from "@/api/system/organization";
import { queryRecords as getMaterielCateList } from "@/api/project/srm/materialClassify/materialMaintain";
export default {
  name: 'treeComponent',
  props: ['type', 'params', 'treeStyleObj'],
  data: function data() {
    return {
      code: '',
      name: '',
      treeData: [],
      defaultProps: {
        org: {
          children: "children",
          label: "organizationName"
        },
        materielCategory: {
          children: "children",
          label: "className"
        }
      }
    };
  },
  mounted: function mounted() {
    this.getTreeList();
  },
  watch: {
    params: {
      handler: function handler() {
        this.getTreeList();
      },
      immediate: true,
      deep: true
    }
  },
  methods: {
    getTreeList: function getTreeList() {
      var _this = this;

      switch (this.type) {
        //组织结构树
        case 'org':
          {
            getOrgList().then(function (response) {
              _this.treeData = _this.$myFun.addTreeRoot(response.data);
            });
            break;
          }
        //物料类型树

        case 'materielCategory':
          {
            if (this.params.orgCode) {
              getMaterielCateList({
                orgCode: this.params.orgCode
              }).then(function (res) {
                _this.treeData = _this.$myFun.addRoot(res.data, 'rowId', 'parentRowId', '物料分类');
              });
            }

            break;
          }
      }
    },
    handleNodeClick: function handleNodeClick(data) {
      switch (this.type) {
        //组织结构树
        case 'org':
          {
            // this.$emit('changeNode', data.Id, data.organizationCode, data.organizationName)
            this.$emit('changeNode', data.rowId);
            break;
          }
        //物料树

        case 'materielCategory':
          {
            this.$emit('changeNode', data.classCode);
            break;
          }
      }
    }
  }
};
