import request from '@/utils/request'; // 查询设备类型

export function queryTypeCode(query) {
  return request({
    url: "/easDevice/base/deviceType",
    method: 'get',
    params: query
  });
} // 查询设备台账信息列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/file/device",
    method: 'get',
    params: query
  });
} // 新增设备台账信息列表

export function createRecords(data) {
  return request({
    url: "/easDevice/file/device",
    method: 'post',
    data: data
  });
} // 编辑设备台账信息列表

export function updateRecords(data) {
  return request({
    url: "/easDevice/file/device",
    method: 'patch',
    data: data
  });
} // 删除设备台账信息列表

export function deleteRecords(deviceCode) {
  return request({
    url: "/easDevice/file/device/".concat(deviceCode),
    method: 'delete'
  });
} // 验证设备台账信息列表

export function CheckUponRecords(state, data) {
  return request({
    url: "/easDevice/file/device/check/".concat(state),
    method: 'patch',
    data: data
  });
}
export function queryRecordsgw(workCenterCode) {
  //查询工位
  return request({
    url: "/easDevice/work-centers/".concat(workCenterCode, "/stations"),
    method: 'get'
  });
}
export function querySubDevice(deviceCode) {
  //查询设备下关联子设备
  return request({
    url: "/easDevice/file/device/deviceRelation/".concat(deviceCode),
    method: 'get'
  });
}
export function addSubDevice(deviceCode, data) {
  //添加设备下关联子设备
  return request({
    url: "/easDevice/file/device/deviceRelation/".concat(deviceCode),
    method: 'post',
    data: data
  });
}
export function deleteDevice(data) {
  //删除设备下关联子设备
  return request({
    url: "/easDevice/file/device/deviceRelation",
    method: 'delete',
    data: data
  });
}
export function uploadFile(functionCode, data, query) {
  //文件上传
  return request({
    url: "/easDevice/files/".concat(functionCode),
    method: 'post',
    data: data,
    params: query // headers: {
    //     'Content-Type': 'multipart/form-data',
    // },

  });
}
export function deleteFile(fileCode) {
  //删除设备文件
  return request({
    url: "/easDevice/files?fileCode=".concat(fileCode),
    method: 'delete'
  });
} // 查询设备履历

