export function queryFile(functionCode, fileType) {
  //查询图片
  return request({
    url: "/easDevice/files?functionCode=".concat(functionCode, "&fileType=").concat(fileType),
    method: 'get'
  });
} // 读取图片

export function queryPic(readName) {
  return request({
    url: "/easDevice/files/get?readName=".concat(readName),
    method: 'get'
  });
} // 文件下载

export function download(readName) {
  return request({
    responseType: 'blob',
    url: "/easDevice/files/download?readName=".concat(readName),
    method: 'get'
  });
} //主表班组信息（查询）

export function groupRecords(query) {
  return request({
    url: "/easDevice/team",
    method: 'get',
    params: query
  });
}
