import request from '@/utils/request';
export function addRecords(data) {
  //保存
  return request({
    url: "/iwms/outStocks",
    method: 'post',
    data: data
  });
}
export function queryRecords(query) {
  //查询交易凭证主表
  return request({
    url: "/iwms/receipt/reversal/source",
    method: 'get',
    params: query
  });
}
export function queryRecordmx(query) {
  //查询交易凭证明细
  return request({
    url: "/iwms/receipt/reversal/source/".concat(query.sourceReceiptCode, "/details"),
    method: 'get',
    params: query
  });
}
export function cancel(data) {
  //冲销
  return request({
    url: "/iwms/receipt/reversal",
    method: 'post',
    data: data
  });
}
export function queryDetails(query) {
  //查询冲销凭证列表
  return request({
    url: "/iwms/receipt/reversal",
    method: 'get',
    params: query
  });
}
export function queryitems(query) {
  //查询冲销凭证明细
  return request({
    url: "/iwms/receipt/reversal/".concat(query.reversalReceiptCode, "/details"),
    method: 'get',
    params: query
  });
}
