import request from '@/utils/request';
export function queryRecords(query) {
  //查询
  return request({
    url: "/mes/routing",
    method: 'get',
    params: query
  });
}
export function addRecord(data) {
  //新增
  return request({
    url: "/mes/routing",
    method: 'post',
    data: data
  });
}
export function getRecord(productProcessCode) {
  //查询单个工艺路线
  return request({
    url: "/mes/routing/".concat(productProcessCode),
    method: 'get'
  });
}
export function updateRecord(productProcessCode, data) {
  //更新工艺路线
  return request({
    url: "/mes/routing/".concat(productProcessCode),
    method: 'patch',
    data: data
  });
}
export function deleteRecord(productProcessCode) {
  //删除
  return request({
    url: "/mes/routing/".concat(productProcessCode),
    method: 'delete'
  });
}
export function queryDetailRecords(productProcessCode) {
  //查询明细
  return request({
    url: "/mes/routing/".concat(productProcessCode, "/detail"),
    method: 'get'
  });
}
export function addDetailRecord(productProcessCode, data) {
  //新增明细
  return request({
    url: "/mes/routing/".concat(productProcessCode, "/detail"),
    method: 'post',
    data: data
  });
}
export function getDetailRecord(productProcessCode, processCode) {
  //查询明细单个工艺路线
  return request({
    url: "/mes/routing/".concat(productProcessCode, "/detail/").concat(processCode),
    method: 'get'
  });
}
export function updateDetailRecord(productProcessCode, processCode, data) {
  //更新明细工艺路线
  return request({
    url: "/mes/routing/".concat(productProcessCode, "/detail/").concat(processCode),
    method: 'patch',
    data: data
  });
}
export function deleteDetailRecord(productProcessCode, processCode) {
  //删除明细
  return request({
    url: "/mes/routing/".concat(productProcessCode, "/detail/").concat(processCode),
    method: 'delete'
  });
}
export function publishRecord(productProcessCode) {
  //发布工艺路线
  return request({
    url: "/mes/routing/".concat(productProcessCode, "/publish"),
    method: 'patch'
  });
}
