export default {
  name: 'echartsCom',
  props: ['list'],
  // 图表数据
  data: function data() {
    return {
      legendData: [],
      // 图例数组
      xAxisData: [],
      // 横坐标日期
      series: [],
      // 折线图数据
      myChart: null // 初始化echarts实例

    };
  },
  methods: {
    // 父组件点击查询时调用
    showEcharts: function showEcharts() {
      var _this = this;

      // 异步获取父组件的数据
      setTimeout(function () {
        // 数据重置
        _this.legendData = [];
        _this.xAxisData = [];
        _this.series = [];
        var deleteTime = [];

        if (_this.list.length > 0) {
          _this.myChart = _this.$echarts.init(document.getElementById('chart'));

          _this.list.map(function (item) {
            _this.xAxisData.push(item.time);

            delete item.time;
            deleteTime.push(item);
          }); // console.log(this.xAxisData)


          _this.legendData = Object.keys(deleteTime[0]); // console.log(this.legendData)

          _this.legendData.map(function (item) {
            var obj = {
              name: item,
              type: 'line',
              stack: '总量',
              smooth: true,
              data: []
            };
            deleteTime.map(function (childItem) {
              obj.data = obj.data.concat(childItem[item]);
            });

            _this.series.push(obj);
          });

          _this.drawChart();
        }
      });
    },
    // 绘制折线图
    drawChart: function drawChart() {
      var option = {
        tooltip: {
          trigger: 'axis'
        },
        legend: {
          data: this.legendData
        },
        toolbox: {
          x: '95%',
          feature: {
            saveAsImage: {}
          }
        },
        xAxis: {
          type: 'category',
          boundaryGap: false,
          data: this.xAxisData
        },
        yAxis: {
          type: 'value'
        },
        series: this.series // 必须指定第二个参数，折线图不合并

      };
      this.myChart.setOption(option, true);
    }
  }
};
