import Pagination from '@/components/Pagination'; // Secondary package based on el-pagination

import { selectlists } from '@/api/system/employees'; // import waves from '@/directive/waves' // Waves directive

export default {
  name: 'personSelect',
  components: {
    Pagination: Pagination
  },
  // directives: { waves },
  props: {
    isTick: {
      type: Boolean,
      default: true //true 为多选

    }
  },
  data: function data() {
    return {
      selectlistRow: undefined,
      tableKey: 0,
      list: [],
      listuser: [],
      total: 0,
      listLoading: true,
      listQuery: {
        currentPage: 1,
        pageSize: 10,
        page: 1
      }
    };
  },
  created: function created() {
    this.handleFilter();
  },
  computed: {
    organizationId: {
      get: function get() {
        return this.organizationId; //获取的时候直接获取值
      },
      set: function set(listQuery) {
        this.organizationId = listQuery.organizationName; //设置的时候变为大写
      }
    }
  },
  methods: {
    handleFilter: function handleFilter() {
      var _this = this;

      //查询组织列表
      this.listQuery.page = 1;
      selectlists(this.listQuery).then(function (response) {
        _this.list = response.data.list;
        _this.total = response.data.pages.count; // Just to simulate the time of the request
      });
    },
    selectRow: function selectRow(val) {
      //备选项选中行
      this.$emit('handSelectPersonnelsRows', val);

      if (this.isTick) {
        this.$emit('handSelectPersonnelsRows', val);
      } else {
        this.selectlistRow = val[val.length - 1];

        if (val.length > 1) {
          this.$refs.tb.clearSelection();
          this.$refs.tb.toggleRowSelection(val[val.length - 1]);
        }

        this.$refs.tb.setCurrentRow(this.selectlistRow); // this.$emit("change", this.selectlistRow)

        this.$emit('handSelectPersonnelsRows', this.selectlistRow); // this.$emit('change', this.selectlistRow)
      }
    }
  }
};
