import * as api from '@/api/data-base/color';
import Pagination from '@/components/Pagination';
import caiGou from '@/components/Select/caigou';
import 'echarts-gl';
export default {
  name: 'failureNumber',
  components: {
    Pagination: Pagination,
    caiGou: caiGou
  },
  data: function data() {
    return {
      list: null,
      subList: null,
      value: undefined,
      total: 0,
      totalSub: 0,
      theight: 0,
      //表格高度
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        colorCode: undefined,
        colorName: undefined
      },
      listQuerySub: {
        page: true,
        currentPage: 1,
        pageSize: 15
      },
      tableKey: [0, 1]
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
    this.getListSub();
    this.drawLine();
  },
  methods: {
    drawLine: function drawLine() {
      var myChart = this.$echarts.init(document.getElementById('myChart'));
      var xAxis = ['故障总数', '及时修复', '暂不维修', '响应超时', '维修超时', '返修次数'];
      var yAxis = ['测试车间1', '测试车间2', '测试车间3', '测试车间4', '测试车间5'];
      var data = [[0, 0, 5], [0, 1, 1], [0, 2, 0], [0, 3, 30], [0, 4, 0], [0, 5, 0], [1, 0, 7], [1, 1, 0], [1, 2, 0], [1, 3, 20], [1, 4, 0], [1, 5, 0], [2, 0, 1], [2, 1, 1], [2, 2, 0], [2, 3, 0], [2, 4, 30], [2, 5, 0], [3, 0, 7], [3, 1, 3], [3, 2, 0], [3, 3, 0], [3, 4, 0], [3, 5, 30], [4, 0, 1], [4, 1, 3], [4, 2, 0], [4, 3, 0], [4, 4, 0], [4, 5, 1]]; // 绘制图表

      myChart.setOption({
        title: {
          text: '故障响应'
        },
        tooltip: {},
        visualMap: {
          max: 20,
          inRange: {
            color: ['#313695', '#4575b4', '#74add1', '#abd9e9', '#e0f3f8', '#ffffbf', '#fee090', '#fdae61', '#f46d43', '#d73027', '#a50026']
          }
        },
        xAxis3D: {
          name: '',
          type: 'category',
          data: xAxis,
          axisLine: {
            lineStyle: {
              opacity: 0
            }
          },
          axisLabel: {
            margin: 20
          },
          axisTick: {
            show: false
          },
          splitLine: {
            show: false
          }
        },
        yAxis3D: {
          name: '',
          type: 'category',
          data: yAxis,
          axisLine: {
            lineStyle: {
              opacity: 0
            }
          },
          axisLabel: {
            margin: 10
          },
          axisTick: {
            show: false
          },
          splitLine: {
            show: false
          }
        },
        zAxis3D: {
          name: '',
          type: 'value',
          max: 30,
          axisLine: {
            lineStyle: {
              opacity: 0
            }
          },
          axisLabel: {
            margin: 20
          },
          axisTick: {
            show: false
          }
        },
        grid3D: {
          boxWidth: 150,
          boxDepth: 80,
          light: {
            main: {
              intensity: 1.2,
              shadow: true
            },
            ambient: {
              intensity: 0.3
            }
          }
        },
        series: [{
          type: 'bar3D',
          data: data.map(function (item) {
            return {
              value: [item[1], item[0], item[2]]
            };
          }),
          shading: 'lambert',
          label: {
            textStyle: {
              fontSize: 16,
              borderWidth: 1
            }
          },
          emphasis: {
            label: {
              textStyle: {
                fontSize: 20,
                color: '#900'
              }
            },
            itemStyle: {
              color: '#900'
            }
          }
        }]
      });
    },
    getList: function getList() {
      var _this2 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this2.list = res.data.list;
        _this2.total = res.data.pages.count;
      });
    },
    getListSub: function getListSub() {
      var _this3 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this3.subList = res.data.list;
        _this3.totalSub = res.data.pages.count;
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 185;
    },
    handleQuery: function handleQuery() {
      //查询
      this.listQuery.currentPage = 1;
      this.getList();
    }
  }
};
