import request from '@/utils/request'; //盘点单主信息查询

export function queryRecords(query) {
  return request({
    url: '/iwms/stores/inventory',
    method: 'get',
    params: query
  });
} //生成盘点单

export function createRecords(data) {
  return request({
    url: '/iwms/stores/inventory',
    method: 'post',
    data: data
  });
} //查询盘点单明细

export function queryRowRecords(query) {
  return request({
    url: "/iwms/stores/inventory/".concat(query.inventoryNo, "/items"),
    method: 'get',
    params: query
  });
} //删除盘点单

export function deleteRecords(data) {
  return request({
    url: "/iwms/stores/inventory",
    method: 'delete',
    data: data
  });
} //更新盘点单/发布

export function updateRecords(data) {
  return request({
    url: "/iwms/stores/inventory",
    method: 'patch',
    data: data
  });
} //盘点单实绩录入

export function realityRecords(data) {
  return request({
    url: "/iwms/stores/inventory/items",
    method: 'patch',
    data: data
  });
} //获取会计周期

export function getAccountDate(query) {
  return request({
    url: "/iwms/accounting",
    method: 'get',
    params: query
  });
}
