import request from '@/utils/request'; // 查询故障代码列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/base/fault/code",
    method: 'get',
    params: query
  });
} // 查询设备部件信息

export function queryParts(typeCode) {
  return request({
    url: "/easDevice/base/part/".concat(typeCode),
    method: 'get'
  });
} // 添加故障代码

export function addRecord(data) {
  return request({
    url: "/easDevice/base/fault/code",
    method: 'post',
    data: data
  });
} // 修改故障代码

export function updateRecord(faultCode, data) {
  return request({
    url: "/easDevice/base/fault/code/".concat(faultCode),
    method: 'patch',
    data: data
  });
} // 删除故障代码

export function deleteRecord(faultCodes) {
  return request({
    url: "/easDevice/base/fault/code/deleteBatch",
    method: 'patch',
    data: faultCodes
  });
}
