import * as api from '@/api/project/eas/fm/failureRegister';
import deviceType from "../../components/deviceTypeSelect";
import Pagination from '@/components/Pagination';
export default {
  name: 'addwxjh2',
  components: {
    Pagination: Pagination,
    deviceType: deviceType
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      options: [],
      supplierData: null,
      listQuery: {
        state: 'fs20',
        currentPage: 1,
        pageSize: 10
      },
      activeName2: 'first',
      listSelected: [],
      tableKey: 0,
      allowSelect: false
    };
  },
  watch: {
    // 监听时间区间数组 写入到对应字段
    listSelected: function listSelected(value) {
      this.$parent.$parent.temp.details = value;
    }
  },
  mounted: function mounted() {// this.getEquipmentData()
    // this.suppierRecords()
  },
  methods: {
    //查设备类型
    getTypeCode: function getTypeCode() {
      var _this = this;

      api.queryTypeCode().then(function (res) {
        _this.options = res.data;
      });
    },
    checked: function checked(i) {
      this.$refs.tb_bx.toggleRowSelection(this.list[i], true);
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getEquipmentData();
      this.listSelected = [];
    },
    getListSelected: function getListSelected() {
      this.listSelected = this.$parent.$parent.temp.details;
    },
    //检查是否取消勾选
    checkUnsel: function checkUnsel(val, row) {
      var isuncheck = true;
      val.forEach(function (item) {
        if (item.faultNumber == row.faultNumber) {
          isuncheck = false;
        }
      });
      return isuncheck;
    },
    selectMain: function selectMain(val, row) {
      var _this2 = this;

      var isuncheck = this.checkUnsel(val, row);

      if (!isuncheck) {
        if (this.listSelected) {
          val.forEach(function (items, indexv) {
            var isSelected = false;

            _this2.listSelected.forEach(function (item) {
              if (item.faultNumber == items.faultNumber) {
                isSelected = true;
              }
            });

            if (!isSelected) {
              _this2.listSelected.push(val[indexv]);
            }
          });
        } else {
          this.listSelected = val;
        }
      } else {
        this.listSelected = this.listSelected.filter(function (item) {
          return item.faultNumber != row.faultNumber;
        });
      }
    },
    selectAll: function selectAll(val) {
      var _this3 = this;

      if (val.length == 0) {
        this.listSelected = [];
        this.$refs.tb_bx.clearSelection();
        return false;
      }

      val.forEach(function (items, indexv) {
        var isSelected = false;

        _this3.listSelected.forEach(function (item) {
          if (item.faultNumber == items.faultNumber) {
            isSelected = true;
          }
        });

        if (!isSelected) {
          _this3.listSelected.push(val[indexv]);
        }
      });
    },
    // 在备选中把已选打钩
    getEquipmentData: function getEquipmentData() {
      var _this4 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this4.list = res.data.list;
        _this4.total = res.data.pages.count; // 已选中的勾选状态

        if (_this4.listSelected) {
          _this4.listSelected.forEach(function (items) {
            _this4.list.forEach(function (item, index) {
              if (item.faultNumber == items.faultNumber) {
                _this4.$nextTick(function () {
                  this.checked(index);
                });
              }
            });
          });
        }
      });
    }
  }
};
