import request from '@/utils/request';
export function queryRecords(query) {
  //查询主表
  return request({
    url: '/iwms/allocations',
    method: 'get',
    params: query
  });
}
export function getRecord(query) {
  //查询明细
  return request({
    url: '/iwms/allocations' + '/' + query.allocNumber + '/items',
    method: 'get',
    params: query
  });
}
export function queryRecordscb(query) {
  //查询从表
  return request({
    url: '/iwms/outStocks',
    method: 'get',
    params: query
  });
}
export function queryRecordsIn(query) {
  //查询入库从表
  return request({
    url: '/iwms/stores/receives',
    method: 'get',
    params: query
  });
}
export function Save(data) {
  //保存
  return request({
    url: '/iwms/outStocks',
    method: 'post',
    data: data
  });
}
export function SaveIn(data) {
  //入库保存
  return request({
    url: '/iwms/stores/receives',
    method: 'post',
    data: data
  });
}
export function queryRecordsmx(query) {
  //查询单个出库凭证明细列表
  return request({
    url: '/iwms/outStocks' + '/' + query.outOrderNumber + '/details',
    method: 'get',
    params: query
  });
}
export function queryRecordsInItems(query) {
  //查询单个出库凭证明细列表(入库)
  return request({
    url: "/iwms/stores/receives/".concat(query.receiveOrderNumber, "/items"),
    method: 'get',
    params: query
  });
}
export function queryRecordsdy(query) {
  //打印出库凭证明细列表
  return request({
    url: '/iwms/outStocks' + '/' + query.outOrderNumber + '/details' + '/' + query.rowNo,
    method: 'get',
    params: query
  });
}
export function deleteRecord(instructCode) {
  //删除
  return request({
    url: '/iwms/plans' + '/salesOut' + '/instructs' + '/' + instructCode,
    method: 'delete'
  });
}
export function updateRecord(instructCode, data) {
  //编辑接口
  return request({
    url: '/iwms/plans' + '/salesOut' + '/instructs' + '/' + instructCode,
    method: 'patch',
    data: data
  });
}
export function queryRecorditems(query) {
  //查询明细
  return request({
    url: '/iwms/plans' + '/salesOut' + '/instructs' + '/' + query.instructCode + '/details',
    method: 'get',
    params: query
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
}
