import getPageTitle from '@/utils/getPageName';
import { queryManufacturers } from "@/api/project/iot/IOT/em";
import { queryProjects } from "@/api/project/iot/IOT/project";
import mainBox from "./main";
import EMTabs from "./EMTabs"; //设备建模 详细tab页

export default {
  name: "IOTEM",
  components: {
    mainBox: mainBox,
    EMTabs: EMTabs
  },
  watch: {
    '$route.params': function $routeParams(newval) {
      if (newval) {
        this.pages = 'main';
        this.code = newval.code;
      }
    }
  },
  data: function data() {
    return {
      pages: 'main',
      code: this.$route.params.code,
      row: undefined,
      // 二级页面选择的行
      title: getPageTitle(this.$route.name),
      manufacturersList: null,
      projectList: null
    };
  },
  created: function created() {
    this.$store.dispatch('iotDict/getDicData', ['iot_state_device', 'iot_attribute_type', 'iot_attribute_company', 'iot_data_type', 'iot_read_type', 'iot_is_standard', 'iot_state', 'iot_nameplate_state']);
    this.getProject();
    this.getTeam();
  },
  methods: {
    setPage: function setPage(pages) {
      this.pages = pages;
    },
    setCode: function setCode(code) {
      this.code = code;
    },
    setRow: function setRow(row) {
      this.row = row;
    },
    getTeam: function getTeam() {
      var _this = this;

      queryManufacturers().then(function (res) {
        _this.manufacturersList = res.data.list;
      });
    },
    getProject: function getProject() {
      var _this2 = this;

      queryProjects().then(function (res) {
        _this2.projectList = res.data;
      });
    }
  }
};
