import request from '@/utils/request'; //查询生产订单接口

export function queryRecords(query) {
  return request({
    url: '/mes/plan/order',
    method: 'get',
    params: query
  });
} //新增检验标准

export function addRecord(data) {
  return request({
    url: '/mes/plan/order',
    method: 'post',
    data: data
  });
} //查询检验标准单明细

export function getRecord(productOrderCode) {
  return request({
    url: "/mes/plan/order/".concat(productOrderCode),
    method: 'get'
  });
} //更新检验标准单明细

export function updateRecord(productOrderCode, data) {
  return request({
    url: "/mes/plan/order/".concat(productOrderCode),
    method: 'patch',
    data: data
  });
} //删除检验标准

export function deleteRecord(productOrderCode) {
  return request({
    url: "/mes/plan/order/".concat(productOrderCode),
    method: 'delete'
  });
} //发布

export function publishRecord(productOrderCode) {
  return request({
    url: "/mes/plan/order/".concat(productOrderCode, "/publish"),
    method: 'patch'
  });
}
