import * as api from '@/api/project/mes/dataBase//warehouse';
import Pagination from '@/components/Pagination';
export default {
  name: 'Color',
  components: {
    Pagination: Pagination
  },
  props: {
    warehouseCode: {
      type: String,
      required: true
    }
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 10,
        warehouseCode: undefined,
        locationCode: undefined,
        locationName: undefined
      },
      temp: {
        locationCode: undefined,
        locationName: undefined,
        warehouseCode: undefined,
        warehouseName: undefined,
        areaCode: undefined
      },
      locationCode: undefined,
      locationName: undefined,
      warehouseName: undefined,
      areaCode: undefined,
      selectCode: [],
      dialogStatus: '',
      tableKey: 0
    };
  },
  mounted: function mounted() {
    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      api.queryLocation(this.warehouseCode, this.listQuery).then(function (res) {
        _this.list = res.data.list;
        _this.total = res.data.pages.count;
      });
    },
    handleQuery: function handleQuery() {
      //查询
      this.listQuery.currentPage = 1;
      this.getList();
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
      this.$emit('handSelectAreasRows', val);
    },
    create: function create() {
      var _this2 = this;

      //新增确认按钮
      this.$refs.temp.validate(function (valid) {
        if (valid) {
          api.addRecord(_this2.temp).then(function () {
            _this2.getList();

            _this2.dialogFormVisible = false;

            _this2.$message({
              title: '成功',
              message: '新增成功',
              type: 'success',
              duration: 2000
            });
          });
        } else {
          return false;
        }
      });
    },
    cancel: function cancel() {
      //取消弹窗按钮
      this.dialogFormVisible = false;
    },
    resetTemp: function resetTemp() {
      this.temp = {
        colorCode: undefined,
        colorName: undefined,
        colorAbbreviation: undefined,
        state: 1,
        remark: undefined
      };
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    },
    handleSelectionChange: function handleSelectionChange(selection) {
      this.selectCode = selection.map(function (v) {
        return v.colorCode;
      });
    }
  }
};
