// 选择通知书编制api
import request from '@/utils/request'; //查询选择通知书列表

export function queryRecords(query) {
  return request({
    url: '/business/select-notice',
    method: 'get',
    params: query
  });
} //分页查询RFQ应答列表

export function queryRFQRecordsCKBJ(query) {
  return request({
    url: '/business/rfq-reply/selectedNotice',
    method: 'get',
    params: query
  });
} // 创建选择通知书

export function createRecords(data) {
  return request({
    url: '/business/select-notice',
    method: 'post',
    data: data
  });
} // 选择通知书参照方案

export function createPlan(data) {
  return request({
    url: '/business/select-notice/reference-plan',
    method: 'post',
    data: data
  });
} //查询单个选择通知书

export function queryRow(noticeNo) {
  return request({
    url: "/business/select-notice/".concat(noticeNo),
    method: 'get'
  });
} // 删除选择通知书

export function deleteRecords(noticeNo) {
  return request({
    url: "/business/select-notice/".concat(noticeNo),
    method: 'delete'
  });
} // 更新选择通知书

export function updateRecords(noticeNo, data) {
  return request({
    url: "/business/select-notice/".concat(noticeNo),
    method: 'patch',
    data: data
  });
} //查询选择物料

export function queryMaterial(noticeNo) {
  return request({
    url: "/business/select-notice/".concat(noticeNo, "/materials"),
    method: 'get'
  });
}
