import request from '@/utils/request'; // 查询

export function queryFlatWind(query) {
  return request({
    url: "/ems/dtPeriodPriceMaintain",
    method: 'get',
    params: query
  });
} // 查询单个

export function queryFlatWindItem(rowId) {
  return request({
    url: "/ems/dtPeriodPriceMaintain/get/".concat(rowId),
    method: 'get'
  });
} // 新增

export function createFlatWind(data) {
  return request({
    url: "/ems/dtPeriodPriceMaintain/create",
    method: 'post',
    data: data
  });
} // 更新

export function updateFlatWind(rowId, data) {
  return request({
    url: "/ems/dtPeriodPriceMaintain/update/".concat(rowId),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteFlatWind(data) {
  return request({
    url: "/ems/dtPeriodPriceMaintain/delete",
    method: 'delete',
    data: data
  });
}
