import * as api from '@/api/project/wms/purchaseStorage/storageCheck';
import caiGou from '@/components/Select/caigou'; //下拉组件

import warehouseSelect from '@/components/Select/warehouse-select'; //下拉组件

import Pagination from "@/components/Pagination";
import { DT_ORG_TYPE } from '@/utils/constant';
export default {
  name: "storageCheck",
  components: {
    caiGou: caiGou,
    warehouseSelect: warehouseSelect,
    Pagination: Pagination
  },
  data: function data() {
    return {
      theight: 0,
      //表格高度
      isSingle: true,
      //表格是否单选 点击各按钮根据流程逻辑控制单多选
      list_a: [],
      total_a: 0,
      areaList: null,
      locatorList: [],
      listQuery_a: {
        currentPage: 1,
        pageSize: 15,
        state: 'iqc2'
      },
      list_a_1: [],
      total_a_1: 0,
      listQuery_a_1: {
        currentPage: 1,
        pageSize: 15
      },
      dto: {},
      DT_ORG_TYPE: DT_ORG_TYPE,
      dialogFormVisible: false,
      temp: {},
      rules: {
        businessType: [{
          required: true,
          message: this.$t('validate.required'),
          trigger: 'blur'
        }]
      }
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight();
    this.getList_a(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    getList_a: function getList_a() {
      var _this2 = this;

      //获取质检单主表
      api.getData_a(this.listQuery_a).then(function (res) {
        res = res.data;
        _this2.list_a = res.list;
        _this2.total_a = res.pages.count;
      });
    },
    getList_a_1: function getList_a_1() {
      var _this3 = this;

      //获取质检单从表
      this.listQuery_a_1.iqcOrderNumber = this.selectlistRow_a.iqcOrderNumber || this.selectlistRow_a.iqcOrderNumber;
      api.getData_a_1(this.listQuery_a_1).then(function (res) {
        res = res.data;
        _this3.list_a_1 = res.list.map(function (v) {
          for (var key in _this3.selectlistRow_a) {
            if (!v.hasOwnProperty(key)) {
              v[key] = _this3.selectlistRow_a[key];
            }
          }

          return v;
        });
        _this3.total_a_1 = res.pages.count;
      });
      api.getArea(this.selectlistRow_a.warehouseCode).then(function (resa) {
        resa = resa.data;
        _this3.areaList = resa;
      });
    },
    getLocatorList: function getLocatorList(warehouseCode, areaCode, i) {
      var _this4 = this;

      api.getLocator(warehouseCode, areaCode).then(function (res) {
        _this4.$set(_this4.locatorList, i, res.data);
      });
    },
    changeAreaSel: function changeAreaSel(areaCode, i) {
      this.getLocatorList(this.selectlistRow_a.warehouseCode, areaCode, i);
    },
    selectRow_a: function selectRow_a(val) {
      //质检单主表选中事件
      this.selectlistRow_a = val[val.length - 1];

      if (val.length > 1) {
        this.$refs.dt_a.clearSelection();
        this.$refs.dt_a.toggleRowSelection(val.pop());
      }

      if (this.selectlistRow_a) {
        this.getList_a_1(this.selectlistRow_a);
      } else {
        this.list_a_1 = [];
        this.total_a_1 = 0;
      }
    },
    selectRow_a_1: function selectRow_a_1(val) {
      //质检从表主表选中事件
      this.selectListRow_a_1 = val;
    },
    clickRow_a: function clickRow_a(val) {
      //质检主表点击事件
      this.selectlistRow_a = val;
      this.$refs.dt_a.clearSelection(); //清除其他行的选中

      this.$refs.dt_a.toggleRowSelection(val); //单击行绑定点击事件
    },
    confirm: function confirm() {
      var result = true;
      this.list_a_1.forEach(function (val) {
        if (val.qualifiedQuantity + val.repairQuantity > 0 && val.nextWareAreaCode == "" && val.nextWareLocationCode == "") {
          result = false;
        }
      });

      if (result) {
        return this.list_a_1;
      } else {
        this.$message({
          title: this.$t("message.warning"),
          message: "请先选择区位和储位",
          type: "warning"
        });
      }
    },
    qualityConfirmation: function qualityConfirmation() {
      var _this5 = this;

      //质检确认
      var a = this.confirm();
      this.dto = {
        iqcOrderNumber: this.selectlistRow_a.iqcOrderNumber,
        method: 'CONFIRM',
        inboundQCDTO: {
          inboundQCItems: a
        }
      };

      if (a) {
        api.update_a(this.dto).then(function () {
          _this5.$message({
            title: "成功",
            message: "质检确认成功",
            type: "success"
          });

          _this5.getList_a();
        });
      }
    },
    qualityRejection: function qualityRejection() {
      var _this6 = this;

      //质检驳回
      this.dto = {
        iqcOrderNumber: this.selectlistRow_a.iqcOrderNumber,
        method: 'REJECT'
      };
      api.update_a(this.dto).then(function () {
        _this6.$message({
          title: "成功",
          message: "质检驳回成功",
          type: "success"
        });

        _this6.getList_a();
      });
    },
    handleRecommend: function handleRecommend() {
      var _this7 = this;

      if (this.selectlistRow_a) {
        if (this.selectListRow_a_1) {
          this.dto.businessType = this.selectlistRow_a.businessType, this.dto.orgCode = this.selectlistRow_a.orgCode, this.dto.wareMaterielDTOS = this.selectListRow_a_1.map(function (v) {
            var DTOSlist = {};
            DTOSlist.materielCode = v.materielCode;
            DTOSlist.materielClassify = "";
            return DTOSlist;
          }), this.dto.warehouseCode = this.selectlistRow_a.warehouseCode, api.recommend(this.dto).then(function (res) {
            res = res.data;
            res.forEach(function (val) {
              _this7.list_a_1.forEach(function (val1, index) {
                if (val.materielCode == val1.materielCode) {
                  _this7.$set(_this7.list_a_1[index], 'nextWareAreaCode', val.areaCode);

                  _this7.$set(_this7.list_a_1[index], 'nextWareLocationCode', val.locationCode);

                  _this7.$set(_this7.list_a_1, index, _this7.list_a_1[index]);
                }
              });
            });
          });
        } else {
          this.$message({
            title: "警告",
            message: "请选择明细信息",
            type: "warning"
          });
        }
      } else {
        this.$message({
          title: "警告",
          message: "请选择一条信息",
          type: "warning"
        });
      }
    }
  }
};
