import { getPurchaseDict, addPurchaseRecords, deletePurchaseRecords, queryPurchaseRecords } from '@/api/data-base/ruleAttrDefine';
export default {
  name: 'contentPurchaseCatalog',
  components: {},
  data: function data() {
    return {
      addList: [],
      theight: 0,
      //表格高度
      tableKey: 0,
      //表格索引
      purchaseList: null,
      stockList: null,
      selectRows: []
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.getSelectData();
    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    getSelectData: function getSelectData() {
      var _this2 = this;

      getPurchaseDict('dt_purchase_class').then(function (res) {
        _this2.purchaseList = res.data.dictItemDTOs;
      });
      getPurchaseDict('dt_stock_type').then(function (res) {
        _this2.stockList = res.data.dictItemDTOs;
      });
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() + 10;
    },
    //显示主表
    getList: function getList() {
      var _this3 = this;

      queryPurchaseRecords().then(function (res) {
        console.log(res);
        _this3.addList = res.data;
      });
    },
    //主表勾选框选中
    selectRow: function selectRow(val) {
      this.selectRows = val;
    },
    //新增
    handleAdd: function handleAdd() {
      var addRow = {
        purchaseClassKey: '',
        //   purchaseCatalogName:'',
        stockTypeKey: '' //   stockTypeName:''

      };
      this.addList.push(addRow);
    },
    //保存
    handleSave: function handleSave() {
      var _this4 = this;

      if (this.selectRows.length === 1) {
        addPurchaseRecords(this.selectRows[0]).then(function () {
          _this4.$message.warning('新增成功');

          _this4.getList();
        });
      } else {
        this.$message.warning('请选择一条信息');
      }
    },
    //删除
    handleDelete: function handleDelete() {
      var _this5 = this;

      if (this.selectRows.length === 1) {
        deletePurchaseRecords(this.selectRows[0].purchaseClassKey).then(function (res) {
          console.log('删除', res);

          _this5.getList();
        }).catch(function (e) {
          console.log(1111, e.message);

          if (e.message === '采购分类对照库存类型不存在') {
            _this5.addList.forEach(function (item, index) {
              _this5.selectRows.forEach(function (item1) {
                if (item1.purchaseClassKey === item.purchaseClassKey) {
                  _this5.addList.splice(index, 1);
                }
              });
            });
          } else {
            throw e;
          }
        });
      } else {
        this.$message.warning('请选择一条信息');
      }
    },
    changePurchase: function changePurchase() {// this.purchaseList.forEach((item,index)=>{
      //     console.log(item,index);
      //     if(item.dictItemKey===purchaseClassKey){
      //         this.addList[i].purchaseCatalogName = item.dictItemValue;
      //     }
      // })
    },
    changeStockType: function changeStockType() {// this.stockList.forEach((item,index)=>{
      //     console.log(item,index);
      //     if(item.dictItemKey===stockTypeKey){
      //         this.addList[i].stockTypeName = item.dictItemValue;
      //     }
      // })
    }
  }
};
