import request from '@/utils/request'; //查询点巡检任务列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/inspection/task",
    method: 'get',
    params: query
  });
} //查询设备点检记录列表

export function queryDetails(query) {
  return request({
    url: "/easDevice/inspection/task/device",
    method: 'get',
    params: query
  });
} //查询设备点检记录列表

export function detailsSearch(inspectionTaskCode, inspectionTaskRecordCode) {
  return request({
    url: "/easDevice/inspection/task/".concat(inspectionTaskCode, "/device/").concat(inspectionTaskRecordCode, "/item"),
    method: 'get'
  });
} //主表跳过

export function mainSkipReason(inspectionTaskCode, skipReason) {
  return request({
    url: "/easDevice/inspection/task/".concat(inspectionTaskCode, "/skip?skipReason=").concat(skipReason),
    method: 'patch'
  });
} //副表跳过

export function fromSkipReason(inspectionTaskCode, inspectionTaskRecordCode, skipReason) {
  return request({
    url: "/easDevice/inspection/task/".concat(inspectionTaskCode, "/skip/").concat(inspectionTaskRecordCode, "/?skipReason=").concat(skipReason),
    method: 'patch'
  });
}
