import request from '@/utils/request'; //平台供应商变更查询

export function queryRecords(query) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder",
    method: 'get',
    params: query
  });
} //平台供应商变更主信息（新增）

export function addRecord(data) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder",
    method: 'post',
    data: data
  });
} //平台供应商变更主信息（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder/".concat(rowId),
    method: 'patch',
    data: data
  });
} //平台供应商变更主信息删除

export function deleteRecord(rowId) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder/".concat(rowId),
    method: 'delete'
  });
} //平台供应商变更明细查询

export function queryRecordsItems(query) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder/".concat(query.changeOrderNo, "/item"),
    method: 'get',
    params: query
  });
} //平台供应商变更明细信息（新增）

export function addRecordItems(changeOrderNo, data) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder/".concat(changeOrderNo, "/item"),
    method: 'post',
    data: data
  });
} //平台供应商变更明细删除

export function deleteRecordItems(changeOrderNo, rowId) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder/".concat(changeOrderNo, "/item/").concat(rowId),
    method: 'delete'
  });
} //平台供应商变更提交审核发布

export function updatePublish(rowId, data) {
  return request({
    url: "/SRMsupplier/supplierChangeOrder/".concat(rowId, "/state"),
    method: 'patch',
    data: data
  });
}
