import request from '@/utils/request'; //问题管理查询

export function queryRecord(query) {
  return request({
    url: "/SRMsupplier/issueList",
    method: 'get',
    params: query
  });
} //问题管理（新增）

export function addRecord(data) {
  return request({
    url: "/SRMsupplier/issueList",
    method: 'post',
    data: data
  });
} //问题管理（编辑）

