import request from '@/utils/request'; // 查询设备产品基本信息列表

export function queryRecords(query) {
  return request({
    url: "/iotIot/iot/device",
    method: 'get',
    params: query
  });
} // 查询单个设备产品基本信息

export function queryRecord(typeCode) {
  return request({
    url: "/iotIot/iot/device/".concat(typeCode),
    method: 'get'
  });
} // 创建设备产品基本信息

export function addRecord(data) {
  return request({
    url: "/iotIot/iot/device",
    method: 'post',
    data: data
  });
} //更新设备产品基本信息

export function updateRecord(typeCode, data) {
  return request({
    url: "/iotIot/iot/device/".concat(typeCode, " "),
    method: 'patch',
    data: data
  });
} // 删除设备产品基本信息

export function deleteRecord(typeCode) {
  return request({
    url: "/iotIot/iot/device/".concat(typeCode),
    method: 'delete'
  });
} // 查询设备模型铭牌信息

