  });
} // 导出

export function downloadModal(excelCode, params, data) {
  return request({
    url: "/iotIot/excels/".concat(excelCode, "/export"),
    method: 'post',
    responseType: 'blob',
    params: params,
    data: data
  });
} //上传excel文件

export function uploadExcel(excelCode, fileFormData, params) {
  return request({
    url: "/iotIot/excels/".concat(excelCode, "/import"),
    method: 'post',
    headers: {
      'Content-Type': 'multipart/form-data'
    },
    data: fileFormData,
    params: params
  });
}
