import request from '@/utils/request'; // 查询设备租借列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/ledger/lease",
    method: 'get',
    params: query
  });
} // 创建设备租借

export function addRecords(data) {
  return request({
    url: "/easDevice/ledger/lease",
    method: 'post',
    data: data
  });
} // 单个设备租借

export function queryOne(leaseOrderCode) {
  return request({
    url: "/easDevice/ledger/lease/".concat(leaseOrderCode),
    method: 'get'
  });
} // 修改设备租借

export function updateRecords(leaseOrderCode, data) {
  return request({
    url: "/easDevice/ledger/lease/".concat(leaseOrderCode),
    method: 'patch',
    data: data
  });
} // 删除设备租借

export function deleteRecords(leaserOrderCode) {
  return request({
    url: "/easDevice/ledger/lease/".concat(leaserOrderCode),
    method: 'delete'
  });
} // 设备租借明细

export function queryDetails(leaseOrderCode) {
  return request({
    url: "/easDevice/ledger/lease/".concat(leaseOrderCode, "/details"),
    method: 'get'
  });
} // 发布

export function publishRecords(leaseOrderCode, method) {
  return request({
    url: "/easDevice/ledger/lease/".concat(leaseOrderCode, "/state?method=").concat(method),
    method: 'patch'
  });
} // 更新返还时间

export function timeRecords(leaseOrderCode, planReturnTime) {
  return request({
    url: "/easDevice/ledger/lease/".concat(leaseOrderCode, "/time?planReturnTime=").concat(planReturnTime),
    method: 'patch'
  });
} // 更新设备租借

export function detailsRecords(leaseOrderCode, rowNo, data) {
  return request({
    url: "/easDevice/ledger/lease/".concat(leaseOrderCode, "/details/").concat(rowNo),
    method: 'patch',
    data: data
  });
}
export function queryRecordsgw(workCenterCode) {
  //查询工位
  return request({
    url: "/easDevice/work-centers/".concat(workCenterCode, "/stations"),
    method: 'get'
  });
}
