import * as api from '@/api/project/ems/deviceManagement/equipmentModel.js';
import Pagination from '@/components/Pagination';
import throttle from 'lodash/throttle';
export default {
  name: 'equipmentModel',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      list: null,
      kindList: null,
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15,
        meterTypeName: undefined,
        meterCode: undefined
      },
      temp: {
        meterTypeCode: undefined,
        meterTypeName: undefined,
        meterCode: undefined,
        remark: undefined
      },
      dialogFormVisible: false,
      dialogStatus: '',
      tableKey: 0,
      total: 0,
      theight: 0,
      //表格高度
      selectlistRow: null,
      rules: {
        meterCode: [{
          required: true,
          message: '请选择设备类别',
          trigger: 'blur'
        }],
        meterTypeCode: [{
          required: true,
          message: '设备型号编码不能为空',
          trigger: 'blur'
        }, {
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }],
        meterTypeName: [{
          required: true,
          message: '设备型号名称不能为空',
          trigger: 'blur'
        }, {
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }],
        remark: [{
          max: 50,
          message: '长度不能超过50',
          trigger: 'blur'
        }]
      }
    };
  },
  mounted: function mounted() {
    this.setTableHeight();
    this.getList();
    this.getKindList();
  },
  destroyed: function destroyed() {
    this.setTableHeight();
    this.getList();
    this.getKindList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      api.queryMeterType(this.listQuery).then(function (res) {
        res.data.list.map(function (v, i) {
          v.serial = i + 1;
        });
        _this.list = res.data.list;
        _this.total = res.data.pages.count;
      });
    },
    getKindList: function getKindList() {
      var _this2 = this;

      api.queryKindList().then(function (res) {
        _this2.kindList = res.data;
      });
    },
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getList();
    },
    handleCreate: function handleCreate() {
      this.resetTemp();
      this.dialogStatus = 'create';
      this.dialogFormVisible = true;
    },
    create: throttle(function () {
      var _this3 = this;

      this.$refs.temp.validate(function (valid) {
        if (valid) {
          api.createMeterType(_this3.temp).then(function () {
            _this3.getList();

            _this3.dialogFormVisible = false;

            _this3.$notify({
              title: '成功',
              message: '新增成功',
              type: 'success',
              duration: 2000
            });
          });
        } else {
          return false;
        }
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    handleUpdate: function handleUpdate() {
      //编辑弹窗
      if (this.selectlistRow && this.selectlistRow.length == 1) {
        this.temp = JSON.parse(JSON.stringify(this.selectlistRow[0])); // copy obj

        this.dialogStatus = 'update';
        this.dialogFormVisible = true;
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    update: throttle(function () {
      var _this4 = this;

      //编辑
      this.$refs['temp'].validate(function (valid) {
        if (valid) {
          api.updateMeterType(_this4.temp.meterTypeCode, _this4.temp).then(function () {
            _this4.getList();

            _this4.dialogFormVisible = false;

            _this4.$message({
              title: '成功',
              message: '更新成功',
              type: 'success',
              duration: 2000
            });
          });
        }
      });
    }, 2000, {
      leading: true,
      trailing: false
    }),
    handleDelete: function handleDelete() {
      var _this5 = this;

      //删除列表
      if (this.selectlistRow && this.selectlistRow.length >= 1) {
        this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(function () {
          var ids = [];

          _this5.selectlistRow.forEach(function (val, index) {
            ids[index] = val.meterTypeCode;
          });

          api.deleteMeterType(ids).then(function () {
            _this5.getList(), _this5.$message({
              title: '成功',
              message: '删除成功',
              type: 'success',
              duration: 2000
            });
          });
        }).catch(function () {
          _this5.$message({
            type: 'info',
            message: '已取消删除'
          });
        });
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    deleteQuery: function deleteQuery() {
      // 清除查询条件
      this.listQuery.currentPage = 1;
      this.listQuery.meterTypeName = undefined;
      this.listQuery.meterCode = undefined;
      this.getList();
    },
    resetTemp: function resetTemp() {
      this.temp = {};
    },
    handleClose: function handleClose() {
      this.resetTemp();
      this.$refs.temp.resetFields();
    },
    setTableHeight: function setTableHeight() {
      //表格高度计算
      this.theight = this.$myFun.getSingleTbHeight();
    },
    selectRow: function selectRow(val) {
      // 获取表格选中时的数据
      this.selectlistRow = val;
    }
  }
};
