import * as api from "@/api/data-base/materiel";
import dictItemSelect from "@/components/Select/dictItemSelect.vue";
import Pagination from '@/components/Pagination';
import { DICT_CODE } from "@/utils/constant";
export default {
  name: 'equipmentSpotCheck',
  components: {
    Pagination: Pagination,
    dictItemSelect: dictItemSelect
  },
  props: {
    // 父组件中已选记录集合
    currentSelect: {
      type: Array,
      default: function _default() {
        return [];
      }
    }
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      options: [],
      DICT_CODE: DICT_CODE,
      supplierData: null,
      listQuery: {
        currentPage: 1,
        pageSize: 10
      },
      activeName2: 'first',
      listSelected: [],
      tableKey: 0,
      allowSelect: false
    };
  },
  mounted: function mounted() {
    this.getEquipmentData(); // this.suppierRecords()
  },
  methods: {
    handleQuery: function handleQuery() {
      this.listQuery.currentPage = 1;
      this.getEquipmentData();
      this.currentSelect = [];
    },
    selectList: function selectList(val) {
      this.selectListRow = val;
      this.$emit("change", this.selectListRow);
    },
    //检查是否取消勾选
    checkUnsel: function checkUnsel(val, row) {
      var isuncheck = true;
      val.forEach(function (item) {
        if (item.materielCode === row.materielCode) {
          isuncheck = false;
        }
      });
      return isuncheck;
    },
    selectMain: function selectMain(val, row) {
      var _this = this;

      var isuncheck = this.checkUnsel(val, row);

      if (!isuncheck) {
        if (this.listSelected) {
          val.forEach(function (items, indexv) {
            var isSelected = false;

            _this.listSelected.forEach(function (item) {
              if (item.materielCode === items.materielCode) {
                isSelected = true;
              }
            });

            if (!isSelected) {
              _this.listSelected.push(val[indexv]);
            }
          });
        } else {
          this.listSelected = val;
        }
      } else {
        this.listSelected = this.listSelected.filter(function (item) {
          return item.materielCode !== row.materielCode;
        });
      }
    },
    selectAll: function selectAll(val) {
      var _this2 = this;

      if (val.length == 0) {
        this.listSelected = [];
        this.$refs.tb_bx.clearSelection();
        return false;
      }

      val.forEach(function (items, indexv) {
        var isSelected = false;

        _this2.listSelected.forEach(function (item) {
          if (item.materielCode === items.materielCode) {
            isSelected = true;
          }
        });

        if (!isSelected) {
          _this2.listSelected.push(val[indexv]);
        }
      });
    },
    getList: function getList() {
      var _this3 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this3.list = res.data.list;
        _this3.total = res.data.pages.count;
      });
    },
    getData: function getData() {
      var _this4 = this;

      this.$refs.tb_bx.clearSelection();
      this.currentSelect.forEach(function (item) {
        _this4.list = _this4.list.filter(function (v) {
          return v.materielCode != item.materielCode;
        });
      });
      this.listSelected = [];
    },
    // 在备选中把已选打钩
    getEquipmentData: function getEquipmentData() {
      var _this5 = this;

      api.queryRecords(this.listQuery).then(function (res) {
        _this5.list = res.data.list;
        _this5.total = res.data.pages.count; // 已选中的勾选状态

        setTimeout(function () {
          _this5.getData();
        }, 0);
      });
    }
  }
};
