import * as api from '@/api/project/eas/el/equipmentArchives';
import Pagination from '@/components/Pagination';
import eqmtSelect from "../../components/eqmtSelect";
export default {
  name: 'dispatchOrderManagement',
  components: {
    Pagination: Pagination,
    eqmtSelect: eqmtSelect
  },
  props: ['deviceCode', 'typeCode', 'selectlist'],
  data: function data() {
    return {
      list: null,
      total: 0,
      theight: 0,
      //表格高度
      childDeviceCode: [],
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 15
      },
      listSelect: [],
      subList: [],
      parentsRow: [],
      importDialogVisible: false,
      tableKey: [0, 1],
      ids: []
    };
  },
  created: function created() {},
  watch: {
    deviceCode: function deviceCode(val) {
      if (val) {
        this.getList();
      }
    }
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };
  },
  methods: {
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight() - 185;
    },
    init: function init(val) {
      this.subList = val;
      this.getList();
    },
    getList: function getList() {
      var _this2 = this;

      if (this.deviceCode) {
        api.querySubDevice(this.deviceCode).then(function (res) {
          _this2.list = res.data;
        });
      }
    },
    handleCreate: function handleCreate() {
      var _this3 = this;

      if (this.selectlist) {
        var arr = this.list.map(function (i) {
          return {
            deviceCode: i.childDeviceCode
          };
        });
        arr.push({
          deviceCode: this.deviceCode
        });
        this.childDeviceCode = arr;
        this.importDialogVisible = true;
        setTimeout(function () {
          _this3.$refs.eqmtSelect.getList();
        }, 0);
      } else {
        this.$message({
          title: '警告',
          message: '请选择设备编码',
          type: 'warning'
        });
      }
    },
    update: function update() {
      var _this4 = this;

      var childDeviceCodes = this.$refs.eqmtSelect.currentSelect.map(function (i) {
        return i.deviceCode;
      });
      api.addSubDevice(this.deviceCode, childDeviceCodes).then(function () {
        _this4.getList();

        _this4.importDialogVisible = false;

        _this4.$message({
          title: '成功',
          message: '新增成功',
          type: 'success',
          duration: 2000
        });
      });
    },
    handleDelete: function handleDelete() {
      var _this5 = this;

      //删除列表
      this.selectlistRow.forEach(function (val, index) {
        _this5.ids[index] = val.rowId;
      });

      if (this.selectlistRow) {
        this.$confirm('此操作将永久删除子设备, 是否继续?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        }).then(function () {
          api.deleteDevice(_this5.ids).then(function () {
            _this5.getList(), _this5.$message({
              title: '成功',
              message: '删除成功',
              type: 'success',
              duration: 2000
            });
          });
        }).catch(function () {
          _this5.$message({
            type: 'info',
            message: '已取消删除'
          });
        });
      } else {
        this.$message({
          title: '警告',
          message: '请选择一条信息',
          type: 'warning'
        });
      }
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
    }
  }
};
