import request from '@/utils/request'; // 查询文件

export function queryFile(query) {
  return request({
    url: "/easDevice/files",
    method: 'get',
    params: query
  });
} // 上传经验文件

export function uploadFile(code, data, query) {
  return request({
    url: "/easDevice/files/".concat(code),
    method: 'post',
    data: data,
    params: query
  });
} // 读取图片

export function queryPic(readName) {
  return request({
    url: "/easDevice/files/get?readName=".concat(readName),
    method: 'get'
  });
} // 读取图片

export function deleteFile(query) {
  return request({
    url: "/easDevice/files",
    method: 'delete',
    params: query
  });
} // 文件下载

export function download(readName) {
  return request({
    responseType: 'blob',
    url: "/easDevice/files/download?readName=".concat(readName),
    method: 'get'
  });
}
