import request from '@/utils/request'; //常量参数查询

export function queryRecords(query) {
  return request({
    url: "/basic/constantParameters",
    method: 'get',
    params: query
  });
} //常量参数维护（新增）

export function addRecord(data) {
  return request({
    url: "/basic/constantParameters",
    method: 'post',
    data: data
  });
} //常量参数维护（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/basic/constantParameters/".concat(rowId),
    method: 'patch',
    data: data
  });
} //常量参数删除

export function deleteRecord(rowId) {
  return request({
    url: "/basic/constantParameters/".concat(rowId),
    method: 'delete'
  });
}
