// 工序维护
import request from '@/utils/request'; // 查询工艺路线列表

export function queryRoute(query) {
  return request({
    url: '/ems/dtRouting',
    method: 'get',
    params: query
  });
} // 创建工艺路线

export function createRoute(data) {
  return request({
    url: '/ems/dtRouting',
    method: 'post',
    data: data
  });
} // 查询单个

export function getRouteCode(routeCode) {
  return request({
    url: "/ems/dtRouting/".concat(routeCode),
    method: 'get'
  });
} // 更新工艺路线

export function updateRoute(routeCode, data) {
  return request({
    url: "/ems/dtRouting/".concat(routeCode),
    method: 'patch',
    data: data
  });
} // 删除

export function deleteRoute(data) {
  return request({
    url: "/ems/dtRouting",
    method: 'delete',
    data: data
  });
} // 工序维护
