import request from '@/utils/request'; // 查询保养标准列表

export function queryRecords(query) {
  return request({
    url: "/easDevice/base/maintenance",
    method: 'get',
    params: query
  });
} //新增

export function addRecord(data) {
  return request({
    url: "/easDevice/base/maintenance",
    method: 'post',
    data: data
  });
} //编辑

export function updateRecord(maintenanceCode, data) {
  return request({
    url: "/easDevice/base/maintenance/".concat(maintenanceCode),
    method: 'patch',
    data: data
  });
} //删除

export function deleteRecord(typeCode) {
  return request({
    url: "/easDevice/base/maintenance/".concat(typeCode),
    method: 'delete'
  });
} // 查询设备类型

export function queryTypeCode(query) {
  return request({
    url: "/easDevice/base/deviceType",
    method: 'get',
    params: query
  });
} // 查询设备部位

export function queryPart(typeCode) {
  return request({
    url: "/easDevice/base/part/".concat(typeCode),
    method: 'get'
  });
}
