import request from "@/utils/request";
export function queryRecords(query) {
  //查询主表
  return request({
    url: "/iwms/plans" + "/" + "pushOrders",
    method: "get",
    params: query
  });
}
export function queryRecorditems(query) {
  //查询明细
  return request({
    url: "/iwms/plans" + "/" + "pushOrders" + "/" + query.pushOrderCode + "/details",
    method: "get",
    params: query
  });
}
export function Save(data) {
  //保存
  return request({
    url: "/iwms/outStocks",
    method: "post",
    data: data
  });
}
export function queryRecordscb(query) {
  //查询从表
