//考察反馈api
import request from '@/utils/request'; // 更新、反馈考察项目

export function updateProjectRecords(investigateNo, rowId, data) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/items/").concat(rowId),
    method: 'patch',
    data: data
  });
} //查询生效反馈

export function queryFeedback(query) {
  return request({
    url: "/SRMsupplier/investigates/feedback-items",
    method: 'get',
    params: query
  });
} //查询已经生成考察计划并已经评分的提案详情

export function queryProposalDetail(query) {
  return request({
    url: "/SRMsupplier/investigates/evaluation-proposal",
    method: 'get',
    params: query
  });
} //查询单个考察项目

export function queryProjectItem(investigateNo, rowId) {
  return request({
    url: "/SRMsupplier/investigates/".concat(investigateNo, "/items/").concat(rowId),
    method: 'get'
  });
}
