import "core-js/modules/web.dom.iterable";
//网关信息
import request from '@/utils/request'; // 查询网关信息列表

export function queryGateways(projectCode, query) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/information"),
    method: 'get',
    params: query
  });
} // 查询单个网关信息

export function queryGateway(projectCode, gatewayCode) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/information/").concat(gatewayCode),
    method: 'get'
  });
} // 创建网关信息信息

export function addGateway(projectCode, data) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/information"),
    method: 'post',
    data: data
  });
} //更新网关信息信息

export function updateGateway(projectCode, gatewayCode, data) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/information/").concat(gatewayCode),
    method: 'patch',
    data: data
  });
} // 删除网关信息信息

export function deleteGateway(projectCode, gatewayCode) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/information/").concat(gatewayCode),
    method: 'delete'
  });
} // 生成序列号

export function getGenerate(projectCode) {
  return request({
    url: "/iotIot/iot/project/".concat(projectCode, "/information/generate"),
    method: 'get'
  });
} // 查询网关异常记录列表

export function getAlarm(query) {
  if (query.alarmTypes && query.alarmTypes.length) {
    var alarmTypes = '';
    query.alarmTypes.forEach(function (item) {
      alarmTypes += '&alarmTypes=' + item;
    });
    delete query.alarmTypes;
    return request({
      url: "/iotIot/iot/project/information/alarm?".concat(alarmTypes),
      method: 'get',
      params: query
    });
  } else {
    return request({
      url: "/iotIot/iot/project/information/alarm",
      method: 'get',
      params: query
    });
  }
} // 获取云平台召读网关子网关通信状态

export function getSnState(pKey, sn) {
  return request({
    url: "/iotIot/iot/project/information/".concat(pKey, "/state/").concat(sn),
    method: 'get'
  });
} //连接密钥下载

export function downloadModal(code) {
  return request({
    responseType: 'blob',
    url: "/iotIot/iot/project/information/download?zipName=".concat(code, ".zip"),
    method: 'get'
  });
}
