import request from '@/utils/request'; //年度降本函查询

export function queryRecords(query) {
  return request({
    url: "/business/cost-reduction",
    method: 'get',
    params: query
  });
} //年度降本函明细查询

export function queryRecordsItems(reductionNo) {
  return request({
    url: "/business/cost-reduction/".concat(reductionNo, "/item"),
    method: 'get'
  });
} // 年度降本函新增

export function createRecords(data) {
  return request({
    url: "/business/cost-reduction",
    method: 'post',
    data: data
  });
} // 年度降本函编辑

export function updateRecords(rowId, data) {
  return request({
    url: "/business/cost-reduction/".concat(rowId),
    method: 'patch',
    data: data
  });
} //年度降本函删除

export function deleteRecord(rowId) {
  return request({
    url: "/business/cost-reduction/".concat(rowId),
    method: 'delete'
  });
} // 年度降本函发布提交

export function submitRecords(data) {
  return request({
    url: "/business/cost-reduction/oper",
    method: 'patch',
    data: data
  });
} // 年度降本函明细保存

export function saveRecords(data) {
  return request({
    url: "/business/cost-reduction/item",
    method: 'patch',
    data: data
  });
} //年度降本函明细信息查询

export function queryRecordItems(query) {
  return request({
    url: "/business/cost-reduction/item",
    method: 'get',
    params: query
  });
} //查询采购价格库信息

export function queryRecordPurchase(query) {
  return request({
    url: "/business/price-library",
