import * as api from '@/api/project/srm/dataBase/replaceDefinition';
import { DICT_CODE } from '@/utils/constant';
import caiGou from '@/components/Select/xtpt/caigou'; //下拉组件

export default {
  components: {
    caiGou: caiGou
  },
  data: function data() {
    return {
      DICT_CODE: DICT_CODE,
      dialogStatus: '',
      dialogVisible: false,
      temp: {
        orgCode: undefined,
        orgName: undefined,
        groupCode: undefined,
        groupName: undefined,
        signatureCode: undefined,
        remark: undefined
      },
      rule: {
        orgCode: [{
          required: true,
          message: '组织编码不能为空',
          trigger: 'blur'
        }],
        groupCode: [{
          required: true,
          message: '分组号不能为空',
          trigger: 'blur'
        }, {
          max: 6,
          message: '分组号不能超过六位！',
          trigger: 'blur'
        }],
        groupName: [{
          required: true,
          message: '分组名称不能为空',
          trigger: 'blur'
        }]
      },
      disabled: false
    };
  },
  methods: {
    //获取组织编码名称
    selectName: function selectName(val) {
      this.temp.orgName = val;
    },
    // 主表新增确认
    create: function create() {
      var _this = this;

      api.addRecord(this.temp).then(function () {
        _this.$message({
          title: '成功',
          message: '新增成功',
          type: 'success',
          duration: 2000
        });

        _this.dialogVisible = false;

        _this.$emit('refresh');
      });
    },
    // 修改确认
    update: function update() {
      var _this2 = this;

      var dto = {
        groupCode: this.temp.groupCode,
        groupName: this.temp.groupName,
        orgCode: this.temp.orgCode,
        orgName: this.temp.orgName,
        signatureCode: this.temp.signatureCode,
        remark: this.temp.remark // console.log(JSON.stringify(dto))

      };
      api.updateRecord(this.temp.rowId, dto).then(function () {
        _this2.$message({
          title: '成功',
          message: '修改成功！',
          type: 'success',
          duration: 2000
        });

        _this2.dialogVisible = false;

        _this2.$emit('refresh');
      });
    },
    resetTemp: function resetTemp() {
      this.temp = {
        orgCode: undefined,
        orgName: undefined,
        groupCode: undefined,
        groupName: undefined,
        signatureCode: undefined,
        remark: undefined
      };
    }
  }
};
