import request from '@/utils/request'; // 查询设备类型信息列表

export function queryRecords(query) {
  return request({
    url: '/easDevice/base/deviceType',
    method: 'get',
    params: query
  });
} // 创建设备类型信息

export function addRecords(data) {
  return request({
    url: '/easDevice/base/deviceType',
    method: 'post',
    data: data
  });
} // 修改设备类型信息

export function patchRecords(data) {
  return request({
    url: '/easDevice/base/deviceType',
    method: 'patch',
    data: data
  });
} // 删除设备类型信息

export function deleteRecords(typeCode) {
  return request({
    url: "/easDevice/base/deviceType/".concat(typeCode),
    method: 'delete'
  });
} // 查询设备部件信息

export function queryRecords2(typeCode) {
  return request({
    url: "/easDevice/base/part/".concat(typeCode),
    method: 'get'
  });
} // 添加设备部件信息

export function addRecords2(data) {
  return request({
    url: "/easDevice/base/part/",
    method: 'post',
    data: data
  });
} // 修改设备部件名称

export function patchRecords2(data) {
  return request({
    url: "/easDevice/base/part/",
    method: 'patch',
    data: data
  });
} // 删除设备部件

export function deleteRecords2(rowId) {
  return request({
    url: "/easDevice/base/part/".concat(rowId),
    method: 'delete'
  });
}
