import request from '@/utils/request';
export function queryRecords(query) {
  //查询
  return request({
    url: "/mes/daq/productionNode",
    method: 'get',
    params: query
  });
}
export function queryWorkOrderCode(workOrderState) {
  //查询随工单
  return request({
    url: "/mes/plan/work/".concat(workOrderState, "/state"),
    method: 'get'
  });
}
export function getRecord(workOrderCode, stationCode) {
  //查询派工单完工信息
  return request({
    url: "/mes/plan/work/".concat(workOrderCode, "/complete/").concat(stationCode),
    method: 'get'
  });
}
export function addRecord(workOrderCode, data) {
  //更新派工单
  return request({
    url: "/mes/plan/work/".concat(workOrderCode),
    method: 'patch',
    data: data
  });
}
