import request from '@/utils/request';
export function addRecords(data) {
  //保存
  return request({
    url: "/iwms/outStocks",
    method: 'post',
    data: data
  });
}
export function queryRecords(query) {
  //查询凭证主表
  return request({
    url: "/iwms/outStocks",
    method: 'get',
    params: query
  });
}
export function queryRecordsitems(query) {
  return request({
    url: "/iwms/outStocks/".concat(query.outOrderNumber, "/details"),
    // url: `/stores/transfers/`,
    method: 'get',
    params: query
  });
} //获取区位

export function getArea(warehouseCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas"),
    method: 'get'
  });
} //获取储位

export function getLocator(warehouseCode, areaCode) {
  return request({
    url: "/base-modeling/warehouses/".concat(warehouseCode, "/areas/").concat(areaCode, "/locations"),
    method: 'get'
  });
}
