import * as api from '@/api/project/eas/ESC/definePointInspectionPlan';
import Pagination from '@/components/Pagination';
import eqmtSelect from "../../components/eqmtSelect";
import pcSelect from "../../components/pointCheckStandardSelect";
export default {
  name: 'tab3',
  components: {
    Pagination: Pagination,
    eqmtSelect: eqmtSelect,
    pcSelect: pcSelect
  },
  data: function data() {
    return {
      list: [],
      total: 0,
      theight: 0,
      //表格高度
      listQuery: {
        page: true,
        currentPage: 1,
        pageSize: 10
      },
      selectlistRow: [],
      tempRow: undefined,
      wlxzDialogVisible: false,
      pcVisible: false,
      tableKey: [0]
    };
  },
  created: function created() {
    this.getList();
  },
  mounted: function mounted() {
    var _this = this;

    this.setTableHeight(); //表格高度自适应

    window.onresize = function () {
      _this.setTableHeight();
    };

    this.getList();
  },
  methods: {
    handleCheck: function handleCheck() {
      if (!this.selectlistRow.length) {
        this.$message({
          title: '警告',
          message: '请选择设备',
          type: 'warning'
        });
        return;
      }

      this.pcVisible = true;
      this.tempRow = undefined;
    },
    handleCheckRow: function handleCheckRow(row) {
      this.pcVisible = true;
      this.tempRow = row;
    },
    getList: function getList() {
      var _this2 = this;

      if (this.$parent.$parent.$parent.selectlistRow.length) {
        api.queryDpipEqmt(this.$parent.$parent.$parent.selectlistRow[0].inspectionPlanCode, this.listQuery).then(function (res) {
          _this2.list = res.data.list;
          _this2.total = res.data.pages.count;
        });
      }
    },
    //表格高度计算
    setTableHeight: function setTableHeight() {
      this.theight = this.$myFun.getSingleTbHeight();
    },
    handleCreate: function handleCreate() {
      var _this3 = this;

      if (!this.$parent.$parent.$parent.selectlistRow.length) {
        this.$message({
          title: '警告',
          message: '请选择计划',
          type: 'warning'
        });
        return false;
      }

      this.wlxzDialogVisible = true;
      setTimeout(function () {
        _this3.$forceUpdate();

        _this3.$refs.eqmtSelect.getList(_this3.$parent.$parent.$parent.selectlistRow[0].orgCode);

        _this3.$refs.eqmtSelect.currentSelect = [];
      }, 0);
    },
    handleDelete: function handleDelete() {
      var _this4 = this;

      if (!this.selectlistRow.length) {
        this.$message({
          title: '警告',
          message: '请选择设备',
          type: 'warning'
        });
        return;
      } //删除列表


      api.deleteDpipEqmt(this.$parent.$parent.$parent.selectlistRow[0].inspectionPlanCode, this.selectlistRow.map(function (item) {
        return item.rowId;
      }).join(',')).then(function () {
        _this4.getList(), _this4.$message({
          title: '成功',
          message: '删除成功',
          type: 'success',
          duration: 2000
        });
      });
    },
    update: function update() {
      var _this5 = this;

      api.addDpipEqmt(this.$parent.$parent.$parent.selectlistRow[0].inspectionPlanCode, this.$refs.eqmtSelect.currentSelect).then(function (res) {
        if (res.status == 201) {
          _this5.getList(_this5.$parent.$parent.$parent.selectlistRow[0].inspectionPlanCode);

          _this5.$message({
            title: '成功',
            message: '添加成功',
            type: 'success',
            duration: 2000
          });

          _this5.wlxzDialogVisible = false;
        }
      });
    },
    updateCheck: function updateCheck() {
      var _this6 = this;

      if (!this.tempRow) {
        api.addEqmtAllCheck(this.$parent.$parent.$parent.selectlistRow[0].inspectionPlanCode, {
          deviceCodes: this.selectlistRow.map(function (item) {
            return item.deviceCode;
          }),
          inspectionPlanDeviceIns: this.$refs.pcSelect.currentSelect
        }).then(function (res) {
          if (res.status == 201) {
            _this6.$message({
              title: '成功',
              message: '点检项批量添加成功',
              type: 'success',
              duration: 2000
            });

            _this6.pcVisible = false;

            _this6.getList();
          }
        });
        return;
      }

      api.addEqmtCheck(this.$parent.$parent.$parent.selectlistRow[0].inspectionPlanCode, this.tempRow.deviceCode, this.$refs.pcSelect.currentSelect).then(function (res) {
        if (res.status == 201) {
          _this6.$message({
            title: '成功',
            message: '点检项添加成功',
            type: 'success',
            duration: 2000
          });

          _this6.pcVisible = false;

          _this6.getList();
        }
      });
    },
    // 获取表格选中时的数据
    selectRow: function selectRow(val) {
      this.selectlistRow = val;
    },
    //主表行点击
    showSubTable: function showSubTable(val) {
      this.$refs.mainList.toggleRowSelection(val, 'selected');
    }
  }
};
