import request from '@/utils/request'; //特殊付款方式查询

export function queryRecords(query) {
  return request({
    url: "/business/payment-special",
    method: 'get',
    params: query
  });
} //特殊付款方式维护（新增）

export function addRecord(data) {
  return request({
    url: "/business/payment-special",
    method: 'post',
    data: data
  });
} //特殊付款方式维护（编辑）

export function updateRecord(rowId, data) {
  return request({
    url: "/business/payment-special/".concat(rowId),
    method: 'patch',
    data: data
  });
} //特殊付款方式删除

export function deleteRecord(data) {
  return request({
    url: "/business/payment-special",
    method: 'delete',
    data: data
  });
} //特殊付款方式维护（生效，审批单，发布）

export function updateEffect(data) {
  return request({
    url: "/business/payment-special/oper",
    method: 'patch',
    data: data
  });
} //特殊付款审批单主信息查询

export function queryRecordsMain(query) {
  return request({
    url: "/business/payment-special/approval-order",
    method: 'get',
    params: query
  });
} //特殊付款审批单明细查询

export function queryRecordsSub(query) {
  return request({
    url: "/business/payment-special/approval-order/".concat(query.approvalNo),
    method: 'get',
    params: query
  });
} //特殊付款方式审批单-审核-撤销

export function updateCancel(approvalNo, data) {
  return request({
    url: "/business/payment-special/approval-order/".concat(approvalNo, "/oper"),
    method: 'patch',
    data: data
  });
}
