import getPageTitle from '@/utils/getPageName';
import { queryProjects } from "@/api/project/iot/IOT/project";
import { queryDeviceBind, updateAllDeliver } from "@/api/project/iot/IOT/db";
import timeData from "./RWTabs/timeData";
import warningEvents from "./RWTabs/warningEvents";
import accountInfo from "./RWTabs/accountInfo";
export default {
  name: "IOTRW",
  components: {
    timeData: timeData,
    warningEvents: warningEvents,
    accountInfo: accountInfo
  },
  watch: {
    '$route.params': function $routeParams(newval) {
      if (newval && newval.code) {
        if (this.code !== newval.code) {
          this.pages = 'main';
          this.code = newval.code;
        }
      } else {
        this.getProject();
      }
    },
    name: function name(val) {
      var arr = this.list.filter(function (res) {
        return res.deviceName == val;
      });
      this.itemData = arr[0];
    },
    projectList: function projectList(val) {
      if (!this.code) {
        this.code = val[0].projectCode;
        this.getList();
      }
    },
    code: function code(val) {
      if (val) {
        this.getList();
      }
    }
  },
  data: function data() {
    return {
      code: this.$route.params.code,
      //一级页面项目编码
      title: getPageTitle(this.$route.name),
      projectList: null,
      list: null,
      activeName: 'timeData',
      name: undefined,
      itemData: undefined,
      subList: null,
      optionData: null,
      expandedKeys: null,
      //默认展开
      checkedKeys: null,
      // 默认勾选
      info: undefined,
      props: {
        rowId: 'rowId',
        label: 'deviceName',
        children: 'children'
      }
    };
  },
  created: function created() {
    this.$store.dispatch('iotDict/getDicData', ['iot_attribute_type', 'iot_nameplate_state', 'iot_state']);
    this.getProject();
  },
  methods: {
    stateClass: function stateClass(state) {
      if (state == '0') {
        return 'iot-success';
      } else if (state == '1') {
        return 'iot-success';
      } else if (state == '2') {
        return 'iot-success';
      }
    },
    // 获取项目
    getProject: function getProject() {
      var _this = this;

      queryProjects().then(function (res) {
        _this.projectList = res.data;
      });
    },
    //获取表格数据
    getList: function getList() {
      var _this2 = this;

      queryDeviceBind(this.code).then(function (response) {
        _this2.list = response.data;

        var treeData = _this2.buildTree(response.data, 'rowId', 'parentId');

        _this2.expandedKeys = treeData.expandedKeys;
        _this2.checkedKeys = treeData.checkedKeys;
        _this2.optionData = treeData.tree;
        _this2.info = treeData.info;
        _this2.name = treeData.checkedKeyName;
      });
    },
    // 获取选中的节点
    getCurrentNode: function getCurrentNode(val) {
      this.$refs.selectTree.setCheckedKeys([val.rowId]);
      this.name = val[this.props.label];
      this.info = val.tipLanguage;
    },
    // 点击节点时候
    nodeClick: function nodeClick(val) {
      if (val.parentId) {
        this.$refs.selectTree.setCheckedKeys([val.rowId]);
        this.name = val[this.props.label];
        this.info = val.tipLanguage;
      } else {
        return;
      }
    },
    // 获取实时数据需要批量交付的数据
    getSubList: function getSubList(data) {
      this.subList = data;
    },
    handleCreate: function handleCreate() {
      var _this3 = this;

      this.$confirm('此操作将交付全部数据, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        var list = _this3.subList.filter(function (i) {
          return i.deliveryState == '未交付';
        });

        var data = list.map(function (i) {
          return i.pointCode;
        });
        updateAllDeliver(_this3.code, _this3.itemData.gatewayCode, _this3.itemData.deviceCode, data).then(function () {
          _this3.$refs.timeData.getList();

          _this3.$message({
            title: '成功',
            message: '交付成功',
            type: 'success',
            duration: 2000
          });
        });
      }).catch(function () {
        _this3.$message({
          type: 'info',
          message: '已取消交付'
        });
      });
    },
    buildTree: function buildTree(array, id, parent_id) {
      var isExpandedKey = true;
      var isCheckedKeys = true;
      var info = undefined; // 创建临时对象

      var temp = {}; // 创建需要返回的树形对象

      var tree = [];
      var expandedKeys = [];
      var checkedKeys = [];
      var checkedKeyName = undefined; // 先遍历数组，将数组的每一项添加到temp对象中

      for (var i in array) {
        temp[array[i][id]] = array[i];
      } // 遍历temp对象，将当前子节点与父节点建立连接


      for (var _i in temp) {
        // 判断是否是根节点下的项
        if (temp[_i][parent_id]) {
          if (!temp[temp[_i][parent_id]].children) {
            temp[temp[_i][parent_id]].children = [];
          }

          if (isCheckedKeys) {
            checkedKeys.push(temp[_i].rowId);
            checkedKeyName = temp[_i].deviceName;
            info = temp[_i].tipLanguage;
            expandedKeys.push(temp[_i].rowId);
            isCheckedKeys = !isCheckedKeys;
          }

          temp[temp[_i][parent_id]].children.push(temp[_i]);
        } else {
          var parTemp = temp[_i];
          parTemp.deviceName = temp[_i].gatewayName;
          parTemp.disabled = true; //禁用

          if (isExpandedKey) {
            expandedKeys.push(temp[_i].rowId);
            isExpandedKey = !isExpandedKey;
          }

          tree.push(parTemp);
        }
      }

      var treeData = {
        expandedKeys: expandedKeys,
        checkedKeys: checkedKeys,
        tree: tree,
        checkedKeyName: checkedKeyName,
        info: info
      };
      return treeData;
    }
  }
};
